import React from 'react';

import { connect } from 'react-redux';
import { filterName } from '../../../../app_state/action_creators/filterActions.jsx';

class SearchMeal extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			meals: []
		}
	}
	handleClick = (userData) => {
		if (userData.length === 1) {
			fetch(`https://www.themealdb.com/api/json/v1/1/search.php?f=${userData}`)
				.then(response => response.json())
				.then(data => this.props.filterName(data));
		}
		else {
			let filteredMeals = this.state.meals.filter(meal => {
				return meal.strMeal.toLowerCase().includes(userData);
			});
			this.setState(() => ({
				meals: filteredMeals
			}));
		}
	}
	componentDidUpdate(prevProps,prevState) {
		if (prevState.meals.length === 0) {
			this.setState(() => ({
				meals: this.props.meals
			}));
		}
			console.log(this.state.meals);
	}
	render() {
		return(
			<React.Fragment>
				<h2><ins>Search Meal</ins></h2>
				<input type="text" onChange={(e) => this.handleClick(e.target.value.toLowerCase())} />
			</React.Fragment>
		);
	}
}

const mapStateToProps = state => {
	return {meals: state.mealsReducer[0]};
}
export default connect(
	mapStateToProps,
	{ filterName }
)(SearchMeal)
