import React from 'react';
import { useHistory } from 'react-router-dom';

import { Tag } from './tag.jsx';

import styles from './static_style.module.css';

const RecipeCard = (props) => {
	const history = useHistory();

	function navigateTo(pathIn) {
		history.push(pathIn);
	}

	return(
		<section className={styles.recipeSection}>
			<header>{ props.meal }</header>
			<aside>
				<span className={styles.category}>
					Category: <span onClick={() => navigateTo(`Categories/${props.category}`)}>{ props.category }</span>
				</span>
				<span className={styles.area}>
					Area: { props.area }
				</span>
			</aside>
			<main className={styles.recipeCard}>
				<figure>
					<img src={props.img} alt={props.meal} />
					<figcaption>- View Source -</figcaption>
				</figure>

				<article>
					<h4>Ingredients</h4>
					{
						props.ingredients.map((ingredient, i) => <p key={i}>{ ingredient }</p> )
					}
				</article>
				{
					props.instructions.split("\r\n").map((instruction, i) => <p key={i}>{ instruction }</p> )
				}
				<footer>
					Tags:
					{
						props.tags.split(",").map((tag, i) => 
							<Tag key={i}>
								{ tag }
							</Tag>
						)
					}
				</footer>
			</main>
			<div className={styles.recipeCardBg}></div>
		</section>
	);
}

export { RecipeCard }
