import React from 'react';

import { Tag } from './tag.jsx';

import styles from './static_style.module.css';

function RecipeCard(props) {
	return(
		<section>
			<header>{ props.meal }</header>

			<main className={styles.recipeCard}>
				<figure>
					<img src={props.img} alt={props.meal} />
					<figcaption>- View Source -</figcaption>
				</figure>

				<article>
					<h4>Ingredients</h4>
					{
						props.ingredients
					}
				</article>

				{
					props.instructions.split("\r\n").map(instruction => 
						<p>instruction</p>
					)
				}
			</main>

			<footer>
				Tags:
				{
					props.tags.split(",").map(tag => 
						<Tag path="">
							{ tag }
						</Tag>
					)
				}
			</footer>
		</section>
	);
}

export { RecipeCard }
